"use client";
import Image from "next/image";
import { useState } from "react";

const SignUpPassword = () => {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  return (
    <div className="bg-primary h-screen flex flex-col items-center justify-center overflow-hidden">
      <div className="flex flex-col items-center justify-center w-full max-w-sm h-[80vh] md:h-full  p-3">
        {/* Logo */}
        <Image
          src="/img/logo.png"
          alt="None"
          width={60}
          height={60}
          className="mb-10"
        />
        <div className="bg-[#252525] px-8 py-12 rounded-lg shadow-2xl w-full">
          <h2 className="text-center text-3xl font-bold mb-6 text-[#FAFAFA] orbitron">
            SIGN <span className="outlined-text text-3xl +">UP</span>
          </h2>
          <div className="flex flex-col w-full items-center justify-center mb-4 ">
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              className="w-full mb-4 px-2 text-sm py-2  rounded-xl bg-[#151515] placeholder-[#8C9092] border border-[#374151]"
            />
            <input
              type="password "
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Enter your full name"
              className="w-full mb-4 px-2 py-2 text-sm rounded-xl bg-[#151515] placeholder-[#8C9092] border border-[#374151]"
            />
            <button className="w-full py-2 rounded-xl text-base text-white font-semibold bg-gradient-to-r from-secondary to-tertiary">
              Continue
            </button>
            <div className="mt-2 text-center text-sm text-[#9CA3AF]">
              Already have an account?
              <a href="/login" className="hover:underline text-[#60A5FA] ml-1">
                Log in
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignUpPassword;
